# Escape codes for terminal colors
# Reset color
reset = "\x1b[0m"

# Datetime color
lime_green = "\x1b[38;2;100;226;46m"    # rgb(100, 226, 46)

# Location color
lila = "\x1b[38;2;174;129;255m"         # rgb(174, 129, 255)

# Level colors
grey = "\x1b[38;2;124;124;124;1m"       # rgb(124, 124, 124)
yellow = "\x1b[38;2;200;200;0;1m"       # rgb(200, 200, 0)
blue = "\x1b[38;2;25;128;255;1m"        # rgb(25, 128, 255)
red = "\x1b[38;2;255;0;0;1m"            # rgb(255, 0, 0)
bold_red = "\x1b[38;2;128;0;0;1m"       # rgb(128, 0, 0)
background_red = "\x1b[48;2;255;0;0;1m" # rgb(255, 0, 0)

# Message color
sand = "\x1b[38;2;246;239;236;1m"       # rgb(246,239,236)